import React, { useState } from "react";
import Header from "./Header.jsx";
import Footer from "./Footer.jsx";
import CreateNote from "./CreateNote.jsx";
import Note from "./Note.jsx";

const App = () => {
  const [addItem, setAddItem] = useState([]);

  const addNote = (note) => {
    // alert("I am clicked");
    setAddItem((oldItem) => {
      return [...oldItem, note];
    });
    // console.log(addItem);
  };

  const DeleteNote = (id) => {
    setAddItem((olddata) =>
      olddata.filter((currdate, indx) => {
        return indx !== id;
      })
    );
  };

  return (
    <>
      <Header />
      <CreateNote passNote={addNote} />

      {addItem.map((val, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            passDelete={DeleteNote}
          />
        );
      })}
      <Footer />
    </>
  );
};

export default App;
